DECLARE @id INT = 0;
DECLARE @maxid INT;
DECLARE @jsonStr VARCHAR(MAX);
DECLARE @col NVARCHAR(100);

SELECT   @maxid= CASE WHEN JSON_VALUE (jsonContent, '$.PageSize') > JSON_VALUE (jsonContent, '$.TotalRecordCount') THEN  JSON_VALUE (jsonContent, '$.PageSize')
        ELSE JSON_VALUE (jsonContent, '$.TotalRecordCount') 
        END 
FROM
    OPENROWSET(
        BULK 'https://dlssynap1.dfs.core.windows.net/lf-data/calisto/agent/AgentData_20210401000000.JSON',
        FORMAT = 'CSV',
        FIELDQUOTE = '0x0b',
        FIELDTERMINATOR ='0x0b',
        ROWTERMINATOR = '0x0b'
    )
    WITH (
        jsonContent varchar(MAX)
    ) AS [result];

WHILE @id <= @maxid
BEGIN
SET @col = N'lax $.Results[' + CAST(@id AS NVARCHAR(50)) + N']';
    SELECT @jsonStr= JSON_QUERY( jsonContent, @col)
FROM
    OPENROWSET(
        BULK 'https://dlssynap1.dfs.core.windows.net/lf-data/calisto/agent/AgentData_20210401000000.JSON',
        FORMAT = 'CSV',
        FIELDQUOTE = '0x0b',
        FIELDTERMINATOR ='0x0b',
        ROWTERMINATOR = '0x0b'
    )
    WITH (
        jsonContent varchar(MAX)
    ) AS [result];

    SET @id = @id + 1;
SELECT JSON_VALUE(@jsonStr, '$.AgentNumber');
END
